import {useState} from 'react';
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import {users} from './userData';
import User from './components/User';
import AddUserFrom from './components/AddUserForm';
import Navbar from './components/Navbar';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
 

// const name= "shafayet";
// const id= "12";
// const dept= "CS";
 


// const names =[ 'a' , 'b', 'c' , 'd']


 


function Userlist(){
 
const [ list, setUsers] = useState(users);


//add user 
const addUser= (newUser)=>{
    setUsers([...list, newUser]);
     
}


//delete user
const deleteUser= (id)=>{
    setUsers(list.filter((user)=>user.id !=id));
}

    return(


        <Router>
			<Navbar/>
			<Route path='/home'>
				<h1> Home </h1>
			 
			</Route>



			<Route path='/add'>
					<AddUserFrom addUser={addUser}/> 
			</Route>

			<Route path='/userlist'>
				<>
						{
							  list.map((user, index)=>{
								return <User key={index} {...user} deleteUser={deleteUser}/>;
							})
						}
				</>		
			</Route>
		</Router>



        
        
    );
}

